import { combineReducers } from "redux";
import postsReducer from "./postsReducer";

/**
 * RULES OF REDUCERS
 * ----------------------------------------------
 * 1) Must return any value but 'undefined'
 *
 */

export default combineReducers({
  posts: postsReducer
});
